    ) VALUES (1572487, 'Tobramycin', 'Phenyl salicylate', 'Moderate', 'The nephrotoxic effect of aminoglycosides may be potentiated by nonsteroidal anti-inflammatory drugs (NSAIDs), particularly if the latter had been given in high dosages for prolonged periods.', NULL, 'It may be advisable to consider reducing the dosage of aminoglycoside prior to initiation of NSAID therapy in infants.', NULL, 'It may be advisable to consider reducing the dosage of aminoglycoside prior to initiation of NSAID therapy in infants.  During coadministration, plasma antibiotic concentrations and renal function should be closely monitored, and the antibiotic dosage further adjusted as necessary.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
