        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573137, 'Carbamazepine', 'Bazedoxifene', 'Moderate', 'Coadministration of bazedoxifene with inducers of uridine diphosphate glucuronosyl transferase (UGT) enzymes may decrease the plasma concentrations of bazedoxifene, which is primarily metabolized by UGT1A1, 1A8, and 1A10, and to a lesser extent, UGT1A9.  A reduction in bazedoxifene exposure may be associated with an increased risk of endometrial hyperplasia.', NULL, 'Dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever a UGT inducer is added to or withdrawn from bazedoxifene therapy.', NULL, 'Dosage adjustments as well as increased clinical and laboratory monitoring should be considered whenever a UGT inducer is added to or withdrawn from bazedoxifene therapy.  If bleeding occurs, appropriate diagnostic measures should be implemented to exclude endometrial hyperplasia.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
