INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573294, 'Ferrous sulfate anhydrous', 'Bictegravir', 'Major', 'Concurrent administration of medications or oral supplements containing polyvalent cations such as aluminum, calcium, iron, or magnesium may decrease the oral bioavailability of bictegravir.', 'DDInter', 1767317477, 'When used with antacids containing aluminum, magnesium or calcium, bictegravir may be taken under fasting conditions 2 hours before the antacids.  Routine administration of bictegravir simultaneously with, or 2 hours after, antacids containing aluminum, magnesium or calcium is not recommended.  When used with supplements containing calcium or iron, bictegravir and the supplements may be taken together with food.  Routine administration of bictegravir under fasting conditions simultaneously with, or 2 hours after, supplements containing calcium or iron is not recommended.', 'Absorption', 'When used with antacids containing aluminum, magnesium or calcium, bictegravir may be taken under fasting conditions 2 hours before the antacids.', NULL, NULL, 'Major', 'pharmacodynamic');
