INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573296, 'Fluconazole', 'Bictegravir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or uridine diphosphate glucuronosyltransferase (UGT) 1A1 may increase the plasma concentrations of bictegravir.  According to the product labeling, bictegravir is a substrate of both CYP450 3A4 and UGT1A1; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', 'DDInter', 1767317477, 'Caution is advised when bictegravir is used with CYP450 3A4 and UGT1A1 inhibitors.  Patients should be monitored for increased adverse effects such as diarrhea, nausea, and vomiting.', 'Metabolism', 'Caution is advised when bictegravir is used with CYP450 3A4 and UGT1A1 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
