INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573300, 'Fostemsavir', 'Bictegravir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of bictegravir.  According to the product labeling, bictegravir is a substrate of both P-gp and BCRP; however, the extent to which each enzymatic pathway contributes to the metabolic clearance of bictegravir has not been reported.', 'DDInter', 1767317477, 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.  If concomitant use is required, caution and close clinical and laboratory monitoring, including renal function, are recommended.  Dosage adjustments may be required whenever a P-gp and/or BCRP inhibitor is added to or withdrawn from therapy.', 'Absorption', 'The use of bictegravir with P-gp and/or BCRP inhibitors should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
