INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573445, 'Bifidobacterium longum infantis', 'Bacampicillin', 'Moderate', 'Coadministration of oral probiotic preparations with oral antibiotics may reduce the efficacy of the probiotic.  It has been theorized that concomitant antibiotics may kill the live organisms found in lactobacillus-, bacillus coagulans-, and bifidobacteria-containing oral probiotic preparations.', 'DDInter', 1767317477, 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', 'Antagonism', 'Although data are limited, it may be prudent to advise patients to take oral probiotics at least 1 to 2 hours before or after an oral antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573446, 'Brentuximab vedotin', 'Lactobacillus acidophilus', 'Moderate', 'Probiotic use during immunosuppressant or intense antineoplastic therapy may theoretically increase the risk of infections from the live microorganisms contained in probiotic products.', 'DDInter', 1767317477, 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.  It may be advisable to avoid using probiotics, particularly products containing saccharomyces boulardii, in patients who are significantly immunosuppressed unless benefits are anticipated to outweigh the potential risk of infection.', 'Others', 'Caution is advised when probiotics are used during immunosuppressant or intense antineoplastic therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
