INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573551, 'Indinavir', 'Norelgestromin', 'Minor', 'Coadministration with indinavir may increase the plasma concentrations of estrogens and progestins.  The proposed mechanism is indinavir inhibition of CYP450 3A4, the isoenzyme involved in the metabolism of estrogens, some (if not all) progestins, and other steroids.', 'DDInter', 1767317477, 'Coadministration with indinavir may increase the plasma concentrations of estrogens and progestins.  The proposed mechanism is indinavir inhibition of CYP450 3A4, the isoenzyme involved in the metabolism of estrogens, some (if not all) progestins, and other steroids.  According to product labeling, indinavir (800 mg every 8 hours) increased the area under the plasma concentration-time curve (AUC) of ethinyl estradiol and norethindrone (administered as Ortho-Novum 1/35 once daily) by approximately 25% each after one week.  The clinical significance of these alterations is unknown but should be considered when selecting oral contraceptive and hormone replacement doses.', 'Metabolism', 'Coadministration with indinavir may increase the plasma concentrations of estrogens and progestins.', NULL, NULL, 'Minor', 'pharmacodynamic');
