INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573555, 'Norelgestromin', 'Ivosidenib', 'Moderate', 'Coadministration with ivosidenib may decrease the plasma concentrations of contraceptive hormones.  The interaction has not been evaluated in pharmacokinetic studies.  However, ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable when used with ivosidenib.  Female patients of reproductive potential receiving hormonal contraceptives should consider alternative or additional methods of contraception during treatment with ivosidenib.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable when used with ivosidenib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
