        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573615, 'Stiripentol', 'Norelgestromin', 'Moderate', 'The concomitant administration of stiripentol may significantly increase plasma concentrations, pharmacologic effects, and toxicities of drugs that are substrates of CYP450 2C19, 3A4, and/or 2D6.  The mechanism is inhibition of cytochrome P-450 metabolism by stiripentol.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.', NULL, 'Clinical and laboratory monitoring for altered efficacy and safety is recommended during concomitant use.  Dosage adjustments may be required if increased plasma levels or effects are observed.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
