        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573621, 'Telotristat ethyl', 'Norelgestromin', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573622, 'Teriflunomide', 'Norelgestromin', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations of contraceptive hormones.  The mechanism of interaction has not been described.', NULL, 'Caution is advised if teriflunomide is prescribed in patients receiving hormonal contraceptives due to the potential for increased adverse effects such as thromboembolism.', NULL, 'Caution is advised if teriflunomide is prescribed in patients receiving hormonal contraceptives due to the potential for increased adverse effects such as thromboembolism.  Consideration should be given to the type or dose of contraceptives used in combination with teriflunomide.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573623, 'Theophylline', 'Norelgestromin', 'Moderate', 'The coadministration with contraceptive steroids may increase the plasma concentrations of theophylline and other methylxanthines.  The proposed mechanism is inhibition of methylxanthine metabolism via hepatic microsomal enzymes, of which estrogens and progestins are also substrates.', NULL, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.', NULL, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.  Serum theophylline levels and pharmacologic response should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their theophylline regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
