        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573846, 'Enoxacin', 'Oxtriphylline', 'Major', 'Enoxacin has been shown to reduce theophylline clearance by up to 75%. The mechanism is inhibition of CYP450 1A2 hepatic metabolism.', NULL, 'If possible, enoxacin therapy should be avoided in patients receiving theophylline.', NULL, 'If possible, enoxacin therapy should be avoided in patients receiving theophylline.  Levofloxacin, sparfloxacin, moxifloxacin, gatifloxacin, trovafloxacin, and lomefloxacin have been reported to cause minor or no changes in theophylline levels and may be considered as alternatives.  If enoxacin is given concomitantly with theophylline, the theophylline dosage should be reduced approximately 50% or more, and theophylline serum levels should be closely monitored.  Patients should be advised to report any signs of theophylline toxicity including nausea, vomiting, diarrhea, headache, restlessness, insomnia, seizures, or irregular heartbeats to their physician.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
