        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1573847, 'Ephedrine (nasal)', 'Oxtriphylline', 'Minor', 'Ephedrine-methylxanthine combinations are used for the treatment of asthma but the efficacy of the combination has been questioned.  This combination may lead to increased xanthine side effects.  The mechanism is unknown, but may be related to synergistic pharmacologic effects.', NULL, 'Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia.', NULL, 'Patients using this combination should be closely monitored for side effects such as nausea, vomiting, tachycardia, nervousness, or insomnia.  If side effects are noted, the dosage of the xanthine may need to be decreased.', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317477);
