INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573918, 'Bumetanide', 'Picosulfuric acid', 'Moderate', 'Coadministration with agents that affect renal function or perfusion such as diuretics, ACE inhibitors, angiotensin receptor blockers, and nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the risk and/or severity of fluid and electrolyte disturbances associated with the use of bowel cleansing preparations.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.  Baseline and postprocedure labs including serum electrolytes, BUN, and creatinine should be considered.  Patients should be advised to drink sufficient quantities of clear fluids before, during, and after bowel cleansing.  Hospitalization and intravenous fluid hydration may be appropriate for frail or elderly patients who may be unable to drink an adequate volume of fluid.', 'Synergism', 'Caution is advised when bowel cleansing preparations are used in patients treated with agents that affect renal function or perfusion, particularly if they are frail or elderly or have preexisting renal impairment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1573919, 'Buprenorphine', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
