INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574143, 'Picosulfuric acid', 'Methdilazine', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574144, 'Picosulfuric acid', 'Methenamine', 'Moderate', 'Theoretically, use of sodium picosulfate during or following antibiotic treatment may result in a reduced laxative effect of sodium picosulfate.  The proposed mechanism involves antibiotic-induced reduction of colonic bacteria that hydrolyze sodium picosulfate, a prodrug, to its active metabolite bis-(p-hydroxyphenyl)-pyridyl-2-methane (BHPM), a compound that stimulates colonic peristalsis.', 'DDInter', 1767317477, 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.  If concomitant use is required, additional bowel cleansing and colonoscopy may be required.', 'Antagonism', 'Consider use of an alternate laxative in patients who have recently taken or are currently taking an antibiotic.', NULL, NULL, 'Moderate', 'pharmacodynamic');
