INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574234, 'Polyethylene glycol (3350)', 'Picosulfuric acid', 'Moderate', 'Concomitant use of stimulant laxatives (e.g., bisacodyl, sodium picosulfate) may increase the risk of serious gastrointestinal adverse effects associated with certain osmotic laxatives (e.g., polyethylene glycol (PEG), oral sulfate solution), such as colonic mucosal ulcerations or ischemic colitis.', 'DDInter', 1767317477, 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.  However, stimulant laxatives, in particular bisacodyl and sodium picosulfate, are sometimes used with PEG in certain bowel cleansing regimens to help reduce dose volume and improve patient tolerability and acceptance.  Please consult individual product labeling for specific recommendations and guidance.  Patients using osmotic bowel cleansing products and stimulant laxatives who present with sudden abdominal pain, rectal bleeding, or other symptoms of ischemic colitis should be evaluated promptly.', 'Synergism', 'The manufacturers for some osmotic bowel cleansing products recommend avoiding the concurrent use of stimulant laxatives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
