INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574262, 'Relugolix', 'Picosulfuric acid', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.  Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly.  Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.  Consideration should be given to consumption of 36 to 48 fluid ounces of a carbohydrate-electrolyte solution in the six hours before the first dose.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574263, 'Remifentanil', 'Picosulfuric acid', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold such as selective serotonin reuptake inhibitors (SSRI antidepressants or anorectics), monoamine oxidase inhibitors, neuroleptic agents, central nervous system stimulants, opioids, tricyclic antidepressants, other tricyclic compounds (e.g., cyclobenzaprine, phenothiazines), systemic steroids, carbapenems, cholinergic agents, fluoroquinolones, interferons, chloroquine, mefloquine, lindane, and theophylline.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, calcium, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold.', NULL, NULL, 'Moderate', 'pharmacodynamic');
