INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574387, 'Colchicine', 'Brentuximab vedotin', 'Moderate', 'Brentuximab vedotin can cause peripheral neuropathy, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317477, 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.  Patients should be closely monitored for symptoms of neuropathy such as burning, tingling, pain, numbness, or weakness.  Patients experiencing new, worsening, or recurrent neuropathy may require a delay, change in dosage, or discontinuation of brentuximab vedotin in accordance with the product labeling.', 'Synergism', 'The potential for increased risk and/or severity of peripheral neuropathy should be considered when brentuximab vedotin is used with other drugs that are also associated with peripheral neuropathy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
