INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574456, 'Cisapride', 'Sodium sulfate', 'Moderate', 'The use of bowel cleansing preparations may increase the risk of ventricular arrhythmia, particularly torsade de pointes, in patients treated with drugs that prolong the QT interval.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.  Monitoring of baseline and posttreatment serum electrolyte levels is recommended, particularly in the elderly.  Patients should be instructed to drink plenty of clear liquids before, during, and after the bowel preparation process.  Consideration should be given to consumption of 36 to 48 fluid ounces of a carbohydrate-electrolyte solution in the six hours before the first dose.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with drugs that prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
