INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574516, 'Fludeoxyglucose (18F)', 'Magnesium sulfate', 'Moderate', 'Certain medications may interfere with the results of diagnostic studies using fludeoxyglucose F18 by increasing or decreasing uptake of the agent into tissues.', 'DDInter', 1767317477, 'Clinicians should be aware of possible diagnostic interference associated with the following medications:  colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', 'Distribution', 'Clinicians should be aware of possible diagnostic interference associated with the following medications:  colony-stimulating factors may increase uptake in spleen and bone marrow; glucose-induced hyperglycemia may interfere with uptake; dopamine and insulin may decrease myocardial and tumor cell uptake; laxative-induced intestinal muscle activity and/or irritation may increase intestinal uptake.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574517, 'Fluoxetine', 'Sodium sulfate', 'Moderate', 'The risk of seizures induced by the use of bowel cleansing preparations may be increased in patients on concomitant medications that can lower the seizure threshold, including psychotherapeutic agents (antidepressants; neuroleptics; lithium), fluoroquinolones, some antimalarial agents (chloroquine; hydroxychloroquine; mefloquine), and some narcotic analgesics.', 'DDInter', 1767317477, 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.  Bowel cleansing preparations should not be used if these patients have impaired renal function or perfusion, dehydration, or uncorrected electrolyte abnormalities.  Baseline and postprocedure labs including serum electrolytes, phosphate, BUN, and creatinine should be considered, particularly in the elderly.  Patients should be advised not to exceed the recommended dosage of their bowel cleansing preparation and to drink sufficient quantities of clear fluids before, during, and after the bowel preparation process.  Administration of an electrolyte rehydration solution may help attenuate the electrolyte abnormalities and hypovolemia.  Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Synergism', 'Caution is advised when bowel cleansing preparations are prescribed in patients treated with agents that can lower the seizure threshold and/or prolong the QT interval.', NULL, NULL, 'Moderate', 'pharmacodynamic');
