INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1574954, 'Dalfopristin', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.  According to some authorities, if the CYP450 3A4 inhibitor cannot be avoided, the dose of neratinib should be reduced to 40 mg once daily with a strong CYP450 3A4 inhibitor and 200 mg once daily with a moderate CYP450 3A4 inhibitor.  The previous dose of neratinib may be resumed following discontinuation of a strong or moderate CYP450 3A4 inhibitor.', 'Metabolism', 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
