INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575119, 'Dasabuvir', 'Darolutamide', 'Moderate', 'Coadministration with darolutamide may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal and hepatobiliary efflux by darolutamide.', 'DDInter', 1767317477, 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible.  If coadministration is required, patients should be monitored carefully for increased adverse reactions of these drugs and dosing adjusted as necessary.', 'Metabolism, Absorption', 'Concomitant use of darolutamide with drugs that are BCRP substrates should be avoided when possible.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575120, 'Darunavir', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of darunavir.  The mechanism of interaction has not been established.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of darunavir plus ritonavir and ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of darunavir plus ritonavir and ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575121, 'Deferasirox', 'Dasabuvir', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.  Patients should be monitored for development of hepatotoxicity (i.e., ALT elevations) and QT interval prolongation.', 'Metabolism', 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575122, 'Delavirdine', 'Paritaprevir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of paritaprevir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors.  Patients should be monitored for signs and symptoms of hepatotoxicity (i.e., ALT and bilirubin elevations).', 'Metabolism', 'Caution is advised if paritaprevir is prescribed in combination with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575123, 'Dicoumarol', 'Paritaprevir', 'Moderate', 'Liver function changes due to treatment with direct-acting HCV antiviral agents may affect the therapeutic efficacy of vitamin K antagonists.  The mechanism may involve potential pharmacokinetic changes for some vitamin K antagonists due to altered metabolism, or patients may exhibit alterations in hepatic synthesis of vitamin K-dependent coagulation factors and clearance of activated factors that could impact response to vitamin K antagonists.', 'DDInter', 1767317477, 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', 'Others', 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
