INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575125, 'Dienestrol (topical)', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', 'DDInter', 1767317477, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter.  If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely.  Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, NULL, 'Major', 'pharmacodynamic');
