INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575132, 'Echinacea', 'Paritaprevir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4.  Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea.  In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317477, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.  If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered.  Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575133, 'Elagolix', 'Dasabuvir', 'Moderate', 'Coadministration with elagolix may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The proposed mechanism is decreased clearance in the intestine, kidney, and/or liver due to inhibition of P-gp-mediated efflux by elagolix.', 'DDInter', 1767317477, 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elagolix is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when elagolix is used concurrently with drugs that are P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575134, 'Eluxadoline', 'Paritaprevir', 'Major', 'Coadministration with inhibitors of organic anion transporting polypeptide (OATP) 1B1 may significantly increase the plasma concentrations of eluxadoline.  Based on available data, inhibition of OATP1B1-mediated hepatic uptake appears to reduce both the hepatic first-pass extraction and systemic biliary clearance of eluxadoline.', 'DDInter', 1767317477, 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors.  Patients should be monitored for adverse effects such as sedation, nausea, vomiting, constipation, abdominal pain, liver enzyme elevations, and pancreatitis.', 'Metabolism', 'The recommended dosage of eluxadoline is 75 mg twice daily when used with OATP1B1 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
