INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575141, 'Entrectinib', 'Paritaprevir', 'Moderate', 'Coadministration with entrectinib may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanisms, based on in vitro data, is decreased clearance due to entrectinib-mediated inhibition of BCRP transport protein.', 'DDInter', 1767317477, 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever entrectinib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when entrectinib is prescribed with drugs that are BCRP substrates, particularly those with a narrow therapeutic range such as methotrexate, mitoxantrone, topotecan, and lapatinib.', NULL, NULL, 'Moderate', 'pharmacodynamic');
