INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575147, 'Ertugliflozin', 'Paritaprevir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575148, 'Ertugliflozin', 'Dasabuvir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575149, 'Esomeprazole', 'Paritaprevir', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole.  The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', 'DDInter', 1767317477, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.  The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575150, 'Esterified estrogens', 'Paritaprevir', 'Major', 'Theoretical concerns exist that coadministration of estrogen-containing products may increase the risk of liver enzyme elevations associated with the use of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  The proposed mechanism is the inhibition of UDP-glucuronosyltransferase (UGT) by ombitasvir, paritaprevir, and dasabuvir.', 'DDInter', 1767317477, 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.  Liver function tests should be performed on all patients during the first 4 weeks of treatment and as clinically indicated thereafter.  If ALT is elevated above baseline at any time during treatment, the test should be repeated and monitored closely.  Therapy should be discontinued if ALT levels remain persistently greater than 10 times the ULN, or if ALT elevations are accompanied by signs or symptoms of liver inflammation or increasing conjugated bilirubin, alkaline phosphatase, or INR.  Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Metabolism', 'Caution is recommended if estrogen-containing products are used in patients receiving ombitasvir/paritaprevir/ritonavir, with or without dasabuvir.', NULL, NULL, 'Major', 'pharmacodynamic');
