INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575176, 'Furosemide', 'Dasabuvir', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may increase the peak plasma concentration (Cmax) of furosemide.  The mechanism of interaction has not been established.', 'DDInter', 1767317477, 'Clinical monitoring of patients is recommended during concomitant use of furosemide with ombitasvir, paritaprevir, ritonavir, and dasabuvir.  Dosage adjustment may be required based on therapeutic response.', 'Others', 'Clinical monitoring of patients is recommended during concomitant use of furosemide with ombitasvir, paritaprevir, ritonavir, and dasabuvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575177, 'Gemfibrozil', 'Dasabuvir', 'Major', 'Coadministration with potent inhibitors of CYP450 2C8 may significantly increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Concomitant use of dasabuvir with potent CYP450 2C8 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of dasabuvir with potent CYP450 2C8 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575178, 'Gemfibrozil', 'Paritaprevir', 'Moderate', 'Coadministration with gemfibrozil may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 transporter.  The mechanism is decreased clearance due to inhibition of OATP 1B1-mediated hepatic uptake by gemfibrozil and its metabolite, gemfibrozil 1-O-beta-glucuronide.', 'DDInter', 1767317477, 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil.  Clinical and laboratory monitoring may be appropriate for some drugs whenever gemfibrozil is added to or withdrawn from therapy.', 'Metabolism, Distribution', 'A dosage reduction of OATP 1B1 substrates may be required when used with gemfibrozil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575179, 'Gilteritinib', 'Paritaprevir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of gilteritinib, which is a substrate of both the isoenzyme and the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.  Patients should be monitored for adverse effects such as QT interval prolongation, pancreatitis, liver transaminase and bilirubin elevations, edema, infections and stomatitis, and the gilteritinib dosage adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when gilteritinib is used with CYP450 3A4 and/or P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
