INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575234, 'Dasabuvir', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) efflux transporter.  Lonafarnib is a weak P-gp inhibitor in vitro.', 'DDInter', 1767317477, 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are sensitive P-gp substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575235, 'Paritaprevir', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 3A4 isoenzyme.  The proposed mechanism is increased clearance due to lorlatinib-mediated induction of CYP450 3A4 metabolism.  The interaction has been studied with midazolam, a sensitive substrate of CYP450 3A4.', 'DDInter', 1767317477, 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy.  Avoid concomitant use with lorlatinib where minimal decreases in concentration of the CYP450 3A4 substrate may lead to serious therapeutic failure.  If coadministration is required, the CYP450 3A4 substrate dosage should be increased in accordance with approved product labeling.', 'Metabolism', 'Caution is advised when lorlatinib is used concomitantly with drugs that are substrates of CYP450 3A4.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575236, 'Dasabuvir', 'Lorlatinib', 'Moderate', 'Coadministration of lorlatinib with drugs that are P-glycoprotein (P-gp) substrates may decrease the plasma concentration and pharmacologic effect of the P-gp substrate.  The proposed mechanism is induction of P-gp mediated drug efflux by lorlatinib.', 'DDInter', 1767317477, 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.  If coadministration is required, consider dosage adjustments and clinical and laboratory monitoring whenever lorlatinib is added to or withdrawn from therapy.  P-gp substrate dosage may need to be increased in accordance with approved product labeling.', 'Absorption', 'Concomitant use of lorlatinib with drugs that are P-gp substrates should generally be avoided, especially those P-gp substrates for which minimal concentration changes may lead to serious therapeutic failures.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575237, 'Dasabuvir', 'Lumateperone', 'Moderate', 'Coadministration of lumateperone with uridine diphosphate glucuronosyltransferase (UGT) inhibitors may increase the plasma concentrations of lumateperone, which is metabolized by the enzyme in vitro.', 'DDInter', 1767317477, 'The use of lumateperone with UGT inhibitors should be avoided.', 'Metabolism', 'The use of lumateperone with UGT inhibitors should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575238, 'Paritaprevir', 'Lusutrombopag', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of lusutrombopag, which is a substrate of both efflux transporter proteins.', 'DDInter', 1767317477, 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.  Pharmacologic response to lusutrombopag should be monitored more closely following initiation, discontinuation, or change of dosage of the concomitant P-gp or BCRP inhibitor.', 'Metabolism', 'Caution is advised if lusutrombopag is prescribed in combination with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
