INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575258, 'Paritaprevir', 'Omeprazole', 'Moderate', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may decrease the plasma concentrations of omeprazole.  The proposed mechanism of interaction is CYP450 2C19 induction by ritonavir.', 'DDInter', 1767317477, 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.  The dosage of omeprazole may be increased in patients whose symptoms are not well controlled; however, some authorities suggest that the dosage of omeprazole should be limited to 40 mg/day.', 'Metabolism', 'Patients should be monitored for potentially diminished efficacy of omeprazole during coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575259, 'Dasabuvir', 'Osimertinib', 'Moderate', 'Coadministration with osimertinib may increase the exposure of breast cancer resistance protein (BCRP) transporter substrates.  In vitro studies have shown that osimertinib is a competitive inhibitor of BCRP.', 'DDInter', 1767317477, 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index.  Monitoring for signs and symptoms of increased exposure to the BCRP substrate should be considered whenever osimertinib is added to or withdrawn from therapy.', 'Absorption', 'Caution is recommended if osimertinib is used in combination with drugs that are substrates of the BCRP transporter, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575260, 'Dasabuvir', 'Ozanimod', 'Minor', 'Breast cancer resistance protein (BCRP) inhibitors are not expected to interact with ozanimod to a clinically relevant extent.', 'DDInter', 1767317477, 'Coadministration of ozanimo with cyclosporine, a BCRP inhibitor, had no effect on the exposure to ozanimo or its major active metabolites, CC112273 and CC1084037, and therefore no adjustment is necessary.', 'Others', 'Coadministration of ozanimo with cyclosporine, a BCRP inhibitor, had no effect on the exposure to ozanimo or its major active metabolites, CC112273 and CC1084037, and therefore no adjustment is necessary.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575261, 'Dasabuvir', 'Pazopanib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.  Patients should be monitored for development of hepatotoxicity (i.e., ALT elevations) and QT interval prolongation.', 'Metabolism', 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575262, 'Dasabuvir', 'Pexidartinib', 'Major', 'Coadministration of pexidartinib with strong CYP450 3A4 inhibitors and/or uridine diphosphate glucuronosyltransferase (UGT) inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity.', 'DDInter', 1767317477, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided.  If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations.  If concomitant use of a strong CYP450 3A4 inhibitor or UGT inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the strong CYP450 3A4 inhibitor or UGT inhibitor, to the dose that was used prior to starting the strong CYP450 3A4 inhibitor or UGT inhibitor.', 'Metabolism', 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575263, 'Pexidartinib', 'Paritaprevir', 'Moderate', 'Coadministration with pexidartinib may decrease the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4.  The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4 isoenzyme.', 'DDInter', 1767317477, 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.  The potential for diminished therapeutic effects should be considered when pexidartinib is prescribed in combination with drugs that are substrates of CYP450 3A4.  Dosage adjustments as well as clinical and laboratory monitoring of the CYP450 3A4 substrate drug should be considered whenever pexidartinib is added to or withdrawn from therapy with these drugs.', 'Metabolism', 'Concomitant administration of pexidartinib with drugs that are substrates of CYP450 3A4 should generally be avoided, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575264, 'Dasabuvir', 'Phenobarbital', 'Major', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, ritonavir, and dasabuvir.', 'DDInter', 1767317477, 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St. John''s wort is considered contraindicated.', 'Metabolism', 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575265, 'Dasabuvir', 'Phenytoin', 'Major', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, ritonavir, and dasabuvir.', 'DDInter', 1767317477, 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St. John''s wort is considered contraindicated.', 'Metabolism', 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575266, 'Pioglitazone', 'Paritaprevir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
