INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575283, 'Dasabuvir', 'Ribociclib', 'Moderate', 'Coadministration with ribociclib may increase the plasma concentrations and pharmacologic effects of drugs that are substrates of breast cancer resistance protein (BCRP), organic cation transporter-2 (OCT-2), multidrug and toxin extrusion 1 (MATE1), and/or bile salt efflux pump (BSEP) transporters.  The proposed mechanism, based on in vitro data, is decreased clearance due to ribociclib-mediated inhibition of these transport proteins at clinically relevant concentrations.', 'DDInter', 1767317477, 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever ribociclib is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if ribociclib is used concomitantly with drugs that are substrates of BCRP, OCT-2, MATE1, and/or BSEP transport proteins, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
