INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575285, 'Rilpivirine', 'Paritaprevir', 'Major', 'Coadministration with ombitasvir/paritaprevir/ritonavir plus dasabuvir may significantly increase the plasma concentrations of rilpivirine.  The mechanism of interaction has not been established, but may involve inhibition of CYP450 3A4-mediated metabolism by ritonavir and paritaprevir.', 'DDInter', 1767317477, 'Concomitant use of rilpivirine with ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', 'Metabolism', 'Concomitant use of rilpivirine with ombitasvir/paritaprevir/ritonavir plus dasabuvir is not recommended.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575286, 'Dasabuvir', 'Rimegepant', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of rimegepant.  The proposed mechanism is decreased clearance due to inhibition of P-gp- and/or BCRP-mediated metabolism of rimegepant.', 'DDInter', 1767317477, 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', 'Metabolism', 'Coadministration of rimegepant with P-gp and/or BCRP inhibitors should be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575287, 'Ripretinib', 'Paritaprevir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of ripretinib and its active metabolite, DP-5439.  In vitro studies indicate that ripretinib and DP-5439 are substrates of the P-gp efflux transporter.', 'DDInter', 1767317477, 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp.  Clinical and laboratory monitoring should be considered whenever a P-gp inhibitor is added to or withdrawn from therapy with ripretinib, and the dosage adjusted as necessary.  Patients should be monitored for development of adverse effects such as nausea, vomiting, constipation, diarrhea, alopecia, arthralgia, myalgia, hypertension, cardiac dysfunction, and palmar-plantar erythrodysesthesia.', 'Absorption', 'Until more information is available, caution is recommended when ripretinib is used concomitantly with drugs that are inhibitors of P-gp.', NULL, NULL, 'Moderate', 'pharmacodynamic');
