INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575289, 'Rolapitant', 'Paritaprevir', 'Moderate', 'Coadministration with rolapitant may increase the plasma concentrations of drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.', 'DDInter', 1767317477, 'Concomitant use of rolapitant with drugs that are BCRP substrates should generally be avoided.  If concomitant administration is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range such as methotrexate, topotecan, and irinotecan.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever rolapitant is added to or withdrawn from therapy.  The lowest effective dose of rosuvastatin should be used when coadministered with rolapitant.', 'Absorption', 'Concomitant use of rolapitant with drugs that are BCRP substrates should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
