INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575311, 'Dasabuvir', 'St. John''s Wort', 'Major', 'Coadministration with potent inducers of CYP450 isoenzymes may significantly decrease the plasma concentrations of ombitasvir, paritaprevir, ritonavir, and dasabuvir.', 'DDInter', 1767317477, 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St. John''s wort is considered contraindicated.', 'Metabolism', 'Concomitant use of ombitasvir/paritaprevir/ritonavir plus dasabuvir with potent CYP450 inducers such as carbamazepine, phenobarbital, phenytoin, rifampin, and St.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575312, 'Tacrolimus', 'Paritaprevir', 'Major', 'Coadministration of ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, may significantly increase the blood concentrations of tacrolimus, sirolimus, and everolimus.  The proposed mechanism involves ritonavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme responsible for the metabolic clearance of these immunosuppressants.  Enhanced oral bioavailability due to inhibition of intestinal P-glycoprotein (P-gp) efflux transporter by ritonavir, paritaprevir, and dasabuvir may also contribute.', 'DDInter', 1767317477, 'Concomitant use of tacrolimus, sirolimus, or everolimus in combination with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, is considered contraindicated.', 'Metabolism', 'Concomitant use of tacrolimus, sirolimus, or everolimus in combination with ombitasvir/paritaprevir/ritonavir, with or without dasabuvir, is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575313, 'Talazoparib', 'Paritaprevir', 'Moderate', 'Coadministration with sparsentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2B6, 2C9, and 2C19 isoenzymes.  The proposed mechanism is increased clearance due to sparsentan-mediated induction of CYP450 2B6, 2C9, and 2C19.', 'DDInter', 1767317477, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.  However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Metabolism', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575314, 'Dasabuvir', 'Talazoparib', 'Moderate', 'Coadministration with sparsentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2B6, 2C9, and 2C19 isoenzymes.  The proposed mechanism is increased clearance due to sparsentan-mediated induction of CYP450 2B6, 2C9, and 2C19.', 'DDInter', 1767317477, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.  However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Metabolism', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575315, 'Tedizolid', 'Paritaprevir', 'Moderate', 'Coadministration with oral tedizolid may increase the plasma concentrations and the risk of adverse effects of orally administered drugs that are substrates of the breast cancer resistance protein (BCRP) transporter.  The proposed mechanism is decreased clearance due to inhibition of BCRP-mediated intestinal efflux by tedizolid.', 'DDInter', 1767317477, 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index.  If coadministration cannot be avoided, monitor for adverse reactions related to concomitantly administered BCRP substrate.', 'Metabolism', 'Consider temporarily interrupting treatment with the BCRP substrate during treatment with tedizolid, especially for BCRP substrates with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
