INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575319, 'Telotristat ethyl', 'Paritaprevir', 'Moderate', 'Coadministration with telotristat ethyl may decrease the plasma concentrations of drugs that are substrates of CYP450 3A4 and/or 2B6 isoenzymes.  The proposed mechanism is accelerated clearance due to induction of CYP450 3A4 and/or 2B6 (in vitro) isoenzymes by telotristat ethyl.', 'DDInter', 1767317477, 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever telotristat ethyl is added to or withdrawn from therapy.', 'Metabolism', 'When drugs that are known substrates of CYP450 3A4 and/or 2B6 are coadministered with telotristat ethyl, the possibility of a diminished therapeutic response to those drugs should be considered.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575320, 'Teriflunomide', 'Paritaprevir', 'Moderate', 'Coadministration with teriflunomide may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of the organic anion transporting polypeptide (OATP) 1B1 and 1B3 and/or breast cancer resistance protein (BCRP) transporters.  The proposed mechanism, based on in vivo data, is decreased clearance due to teriflunomide-mediated inhibition of OATP 1B1/1B3 or BCRP transport proteins.', 'DDInter', 1767317477, 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OATP 1B1/1B3 and/or BCRP.  Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever teriflunomide is added to or withdrawn from therapy with these drugs.  Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised if teriflunomide is used concomitantly with drugs that are substrates of OATP 1B1/1B3 and/or BCRP.', NULL, NULL, 'Moderate', 'pharmacodynamic');
