INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575336, 'Dasabuvir', 'Tucatinib', 'Moderate', 'Coadministration with tucatinib may increase the plasma concentrations of drugs that are substrates of the P-glycoprotein (P-gp) transporter.  The mechanism likely involves tucatinib-mediated inhibition of P-gp.', 'DDInter', 1767317477, 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index.  A dose reduction of the P-gp substrates as well as clinical and laboratory monitoring may be appropriate.', 'Absorption', 'Caution is advised if tucatinib is used concomitantly with P-gp substrates, particularly those with a narrow therapeutic index.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575337, 'Dasabuvir', 'Ubrogepant', 'Moderate', 'Coadministration with breast cancer resistance protein (BCRP) and/or P-glycoprotein (P-gp) only inhibitors may increase the exposure of ubrogepant, which is a substrate of these transporters.', 'DDInter', 1767317477, 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors.  If needed, a second 50 mg dose of ubrogepant may be administered at least 2 hours after the initial dose.', 'Metabolism', 'The manufacturer recommends an initial ubrogepant dose of 50 mg when coadministered with BCRP and/or P-gp only inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575338, 'Valsartan', 'Paritaprevir', 'Moderate', 'Coadministration with inhibitors of the hepatic uptake transporter OATP 1B1 (e.g., rifampin, cyclosporine, paritaprevir) or the hepatic efflux transporter MRP2 (e.g., ritonavir) may increase the systemic exposure to valsartan, which is a substrate of both transporters.', 'DDInter', 1767317477, 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2.  Pharmacologic response and blood pressure should be monitored more closely following the addition, discontinuation, or change of dosage of the transporter inhibitor, and the valsartan dosage adjusted as necessary.', 'Distribution', 'Caution is advised if valsartan is used in combination with inhibitors of OATP 1B1 or MRP2.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575339, 'Dasabuvir', 'Vemurafenib', 'Moderate', 'Coadministration with inhibitors of CYP450 2C8 may increase the plasma concentrations of dasabuvir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.  Patients should be monitored for development of hepatotoxicity (i.e., ALT elevations) and QT interval prolongation.', 'Metabolism', 'Caution is advised if dasabuvir is prescribed in combination with CYP450 2C8 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575340, 'Venetoclax', 'Paritaprevir', 'Major', 'Coadministration with P-glycoprotein (P-gp) inhibitors may increase the plasma concentrations of venetoclax, which is a substrate of this efflux transporter.', 'DDInter', 1767317477, 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided.  If coadministration is required, the manufacturer recommends that venetoclax dosage be reduced by at least 50%.  The dosage used prior to initiating the P-gp inhibitor may be resumed 2 to 3 days after discontinuation of the inhibitor.', 'Absorption', 'Concomitant use of venetoclax with P-gp inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575341, 'Dasabuvir', 'Venetoclax', 'Moderate', 'Coadministration with venetoclax may increase the plasma concentrations of drugs that are substrates of P-glycoprotein (P-gp).  The proposed mechanism is decreased clearance due to inhibition of P-glycoprotein-mediated drug efflux in the intestine, liver, and/or kidney by venetoclax.', 'DDInter', 1767317477, 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.  Alternatives should be considered whenever possible.  The manufacturer recommends that drugs sensitive to P-gp inhibition in the gastrointestinal tract be administered at least 6 hours before venetoclax.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate following the initiation or discontinuation of venetoclax.', 'Metabolism, Absorption, Excretion', 'Caution is advised when venetoclax is prescribed with drugs that are P-gp substrates, particularly those with a narrow therapeutic range such as digoxin and dabigatran etexilate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
