c ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575389, 'Dienogest', 'Chlorpropamide', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575390, 'Dienogest', 'Cholestyramine', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins.  The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine.', 'DDInter', 1767317477, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.  Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575391, 'Dienogest', 'Cladribine', 'Moderate', 'Coadministration with cladribine may reduce the efficacy of hormonal contraceptives.', 'DDInter', 1767317477, 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.  Women should avoid becoming pregnant during treatment with cladribine and for at least 6 months after the last dose in each treatment course.  Women using hormonal contraceptives should add a barrier method during cladribine dosing and for at least 4 weeks after the last dose in each treatment cycle.  In addition, male patients should take precautions to prevent pregnancy of their partner during cladribine dosing and for at least 6 months after the last dose in each treatment course.', 'Antagonism', 'Although the clinical significance of this interaction is unknown, caution is advised when cladribine is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575392, 'Dienogest', 'Clarithromycin', 'Moderate', 'The effectiveness of estrogen-containing oral contraceptives may be impaired by concomitant treatment with antimicrobial agents.', 'DDInter', 1767317477, 'During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', 'Others', 'During concomitant therapy with erythromycin or clarithromycin, patients should be observed for increased or altered pharmacologic response to estrogens and progestogens, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575393, 'Dienogest', 'Clotrimazole', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins.  The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317477, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
