INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575394, 'Dienogest', 'Clozapine', 'Moderate', 'Limited data suggest that coadministration with contraceptive hormones may increase the plasma concentrations of clozapine.  The exact mechanism of interaction is unknown but may involve inhibition of clozapine metabolism via CYP450 1A2, 2C19 and/or 3A4.', 'DDInter', 1767317477, 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.  Pharmacologic response to clozapine should be monitored more closely whenever an oral contraceptive is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the clozapine dosage adjusted as necessary.', 'Metabolism', 'Patients treated concomitantly with hormonal contraceptives may require a lower dosage of clozapine to control psychotic symptoms.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575395, 'Dienogest', 'Cobicistat', 'Moderate', 'Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones.  The exact mechanism of interaction has not been described.', 'DDInter', 1767317477, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.  Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended.  Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide.  However, the long term effects of increased progesterone exposure are not known.  Alternative, nonhormonal methods of contraception may be considered.', 'Others', 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575396, 'Dienogest', 'Colesevelam', 'Moderate', 'Colesevelam may decrease the gastrointestinal absorption of contraceptive hormones.', 'DDInter', 1767317477, 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose.  The same precaution may be applicable to other contraceptive hormones, although data are lacking.', 'Absorption', 'Oral contraceptives containing ethinyl estradiol and norethindrone should be administered at least 4 hours before the colesevelam dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575397, 'Dienogest', 'Colestipol', 'Moderate', 'Coadministration with bile-acid binding resins such as cholestyramine may decrease the plasma concentrations and therapeutic effects of estrogens and progestins.  The proposed mechanism involves delaying or reducing the absorption of concomitant orally administered drugs by cholestyramine.', 'DDInter', 1767317477, 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.  Patients receiving this combination should be advised to closely follow dosing schedules to prevent loss of therapeutic effects.', 'Absorption', 'Estrogens and/or progestin-containing oral medications should be administered at least one hour before or four to six hours after bile-acid binding resins such as cholestyramine, colestipol, and colesevelam.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575398, 'Dienogest', 'Conestat alfa', 'Moderate', 'Thromboembolism may occur during treatment with C1 esterase inhibitors, and concurrent use of other agents that are also associated with this adverse effect can potentiate the risk.', 'DDInter', 1767317477, 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.  Close monitoring for thromboembolic events is recommended during and after administration of C1 esterase inhibitors.  Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain; shortness of breath; rapid pulse; pain, swelling, and/or discoloration in an arm or leg; and numbness or weakness on one side of the body.', 'Synergism', 'Caution is advised when C1 esterase inhibitors are used with other drugs that have thrombogenic effects such as androgens, estrogens, erythropoiesis-stimulating agents, or selective estrogen receptor modulators.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575399, 'Dienogest', 'Cyclosporine', 'Moderate', 'Coadministration with some estrogens or progestins may increase the plasma concentrations of cyclosporine.  The risk of cyclosporine-induced nephrotoxicity, hepatotoxicity, and neurotoxicity may be increased.  The proposed mechanism is competitive inhibition of cyclosporine metabolism via CYP450 3A4, of which estrogens and progestins are also substrates.', 'DDInter', 1767317477, 'Caution is advised during concomitant therapy with cyclosporine.  Cyclosporine blood levels as well as renal and hepatic function should be checked frequently and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their cyclosporine regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity (e.g., fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, jaundice) or neurotoxicity (e.g., tremor, convulsions).', 'Metabolism', 'Caution is advised during concomitant therapy with cyclosporine.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575400, 'Dienogest', 'Dabrafenib', 'Major', 'Coadministration with dabrafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The mechanism is dabrafenib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.  Because use of dabrafenib may cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Female patients of reproductive potential should use a highly effective, non-hormonal method of contraception during treatment and for 4 weeks after treatment.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with dabrafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575401, 'Dienogest', 'Dapagliflozin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575402, 'Dienogest', 'Deferasirox', 'Moderate', 'Coadministration with deferasirox may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is deferasirox induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317477, 'Hormonal contraceptives agents may not be reliable when used with deferasirox.  Female patients of reproductive potential receiving hormonal contraceptives should be counseled to use alternative or additional methods of birth control during treatment with deferasirox.', 'Metabolism', 'Hormonal contraceptives agents may not be reliable when used with deferasirox.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575403, 'Dienogest', 'Valproic acid', 'Moderate', 'A case report suggests that estrogens or progestins may decrease the serum concentrations and pharmacologic effects of valproic acid (VPA).  The proposed mechanism is induction of hepatic glucuronidation by sex hormones.', 'DDInter', 1767317477, 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.  Patients should be advised to contact their physician if they experience loss of seizure control or symptoms of valproic acid toxicity such as tremors, ataxia, nystagmus, increased seizures, and changes in mental status.  In patients receiving oral contraceptives, gradual transient increases in valproic acid levels will likely occur during the pill-free week for women not also taking an enzyme-inducing drug (e.g., carbamazepine, phenytoin, phenobarbital, primidone, rifampin).  The increase in valproic acid levels will be greater if the dose of valproic acid is increased in the few days before or during the pill-free week.', 'Metabolism', 'Pharmacologic response and serum valproic levels should be monitored more closely whenever estrogen- and/or progestin-containing drugs are added to or withdrawn from therapy, and the valproic acid dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575404, 'Dulaglutide', 'Dienogest', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575405, 'Efavirenz', 'Dienogest', 'Moderate', 'There are limited data concerning the potential effect of efavirenz on hormonal contraceptives.  Presumably, coadministration with efavirenz may decrease the efficacy of contraceptives containing low-dose estrogens and progestins due to its induction of CYP450 3A4, the isoenzyme that is primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317477, 'Because the potential interaction between efavirenz and contraceptive hormones has not been fully characterized, and use of efavirenz has been associated with fetal malformations in animal studies, the manufacturer recommends that women avoid pregnancy during efavirenz treatment and that a barrier form of contraception be used in addition to hormonal contraceptives.  Alternative or additional methods of birth control should be used during and for at least 4 weeks after efavirenz therapy.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Because the potential interaction between efavirenz and contraceptive hormones has not been fully characterized, and use of efavirenz has been associated with fetal malformations in animal studies, the manufacturer recommends that women avoid pregnancy during efavirenz treatment and that a barrier form of contraception be used in addition to hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575406, 'Elagolix', 'Dienogest', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of estrogens and progestins.  Estrogens have been shown in in vitro and in vivo studies to be partially metabolized by CYP450 3A4, and other steroids including progestins are also believed to undergo metabolism by this isoenzyme.  The interaction has been reported primarily with oral contraceptives.', 'DDInter', 1767317477, 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.  For patients receiving hormonal contraceptives, additional or alternative non-hormonal birth control may be advisable during concomitant therapy with CYP450 3A4 inducers.  Additional or alternative non-hormonal birth control may be recommended beyond discontinuation of the CYP450 3A4 inducer(s).  Individual product labeling should be consulted for specific time frames.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Patients should be advised to notify their physician if they experience inadequate control of symptoms associated with estrogen deficiency (e.g., nocturnal sweating, vasomotor disturbances, atrophic vaginitis) or changes in the uterine bleeding profile.', 'Metabolism', 'Pharmacologic response to estrogens and progestins should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the hormone dosage adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575407, 'Elvitegravir', 'Dienogest', 'Moderate', 'Coadministration with cobicistat and elvitegravir may alter the plasma concentrations of contraceptive hormones.  The exact mechanism of interaction has not been described.', 'DDInter', 1767317477, 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.  Coadministration of cobicistat and elvitegravir with other hormonal contraceptives (e.g., contraceptive patch, contraceptive vaginal ring, or injectable contraceptives), oral contraceptives containing progestogens other than norgestimate, or less than 25 mcg of ethinyl estradiol has not been studied and is not recommended.  Some authorities recommend that hormonal contraceptives containing at least 30 mcg of ethinyl estradiol with norgestimate should be used in combination with the multi-ingredient antiretroviral formulations containing elvitegravir/cobicistat/emtricitabine/tenofovir disoproxil fumarate or elvitegravir/cobicistat/emtricitabine/tenofovir alafenamide.  However, the long term effects of increased progesterone exposure are not known.  Alternative, nonhormonal methods of contraception may be considered.', 'Others', 'The potential risks and benefits of using norgestimate-containing contraceptives in combination with cobicistat and elvitegravir should be considered, particularly in women who have risk factors for insulin resistance, dyslipidemia, acne, and venous thrombosis.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575408, 'Emapalumab', 'Dienogest', 'Moderate', 'Plasma concentrations of drugs that are CYP450 substrates may decrease following the initiation of interleukin (IL) inhibitors, tumor necrosis factor (TNF) blockers, or interferon (IFN) inhibitors in patients with chronic inflammatory diseases.  Because the formation of hepatic CYP450 enzymes is down-regulated during infection and chronic inflammation by increased levels of certain cytokines (e.g., interleukins-1, -6, and -10; tumor necrosis factor alpha; interferons), treatment targeting these cytokines may restore or normalize CYP450 enzyme levels resulting in increased metabolism of these drugs.', 'DDInter', 1767317477, 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.g., antiarrhythmics, anticonvulsants, immunosuppressants, theophylline) or sensitive substrates where decreases in plasma levels may be significant or undesirable (e.g., oral contraceptives, statins, benzodiazepines, opioids).  Clinical and/or laboratory monitoring should be considered following the initiation or withdrawal of such treatments, and the dosage(s) of the CYP450 substrate(s) adjusted accordingly.  Clinicians should note that the effects of IL inhibitors, TNF blockers, and IFN inhibitors on CYP450 activities may persist for several weeks after stopping therapy.', 'Metabolism', 'Caution is advised when treatments targeting cytokines such as interleukins, tumor necrosis factors, or interferons are prescribed to patients receiving concomitant drugs that are CYP450 substrates, particularly those with narrow therapeutic ranges (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575409, 'Empagliflozin', 'Dienogest', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, sirolimus, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents.  These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317477, 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.  Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.  Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.  Likewise, patients should be observed for hypoglycemia when these drugs are withdrawn from their therapeutic regimen.', 'Antagonism', 'Caution is advised when drugs that can interfere with glucose metabolism are prescribed to patients with diabetes.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575410, 'Enasidenib', 'Dienogest', 'Moderate', 'Coadministration with enasidenib may alter the plasma concentrations of hormonal contraceptives.  In vitro, enasidenib has been found to both inhibit and induce CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317477, 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.  It may be advisable to monitor patients for potentially altered effects of their hormonal contraceptive during and after treatment with enasidenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Because use of enasidenib may be associated with fetal harm, it is important that patients not become pregnant during treatment.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575411, 'Encorafenib', 'Dienogest', 'Major', 'Coadministration with encorafenib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317477, 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.  Because encorafenib can cause fetal harm, it is particularly important that patients not become pregnant during treatment.  Therefore, females of reproductive potential should use an effective, nonhormonal method of contraception during treatment and for 2 weeks after the final dose of encorafenib.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Metabolism', 'Hormonal contraceptives, including oral, injectable, transdermal, and implantable forms, may not be reliable during concomitant therapy with encorafenib.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575412, 'Entrectinib', 'Dienogest', 'Moderate', 'Coadministration with entrectinib may render systemic hormonal contraceptives ineffective.  The mechanism for the potential interaction has not been established and there are no clinical data regarding the use of entrectinib with hormonal contraceptives.', 'DDInter', 1767317477, 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.  In addition, men receiving entrectinib should use highly effective contraceptive methods during treatment and for 3 months after the final dose.', 'Others', 'Until further data are available, females of reproductive potential should be advised to use effective non-hormonal contraception during treatment and for 5 weeks after the final dose.', NULL, NULL, 'Moderate', 'pharmacodynamic');
