INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575421, 'Flibanserin', 'Dienogest', 'Moderate', 'Concomitant use of hormonal contraceptives has been associated with increased adverse effects of flibanserin.  The mechanism of interaction has not been established, but may partially involve inhibition of flibanserin metabolism.  Flibanserin is primarily metabolized by CYP450 3A4 and, to a lesser extent, by CYP450 2C19.  Some oral contraceptives are considered weak inhibitors of these isoenzymes.', 'DDInter', 1767317477, 'Caution is advised when flibanserin is used with hormonal contraceptives.  Patients should be monitored for signs and symptoms of hypotension, syncope, and central nervous system depression.', 'Metabolism', 'Caution is advised when flibanserin is used with hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575422, 'Fluconazole', 'Dienogest', 'Moderate', 'Azole antifungal agents may increase the plasma concentrations of estrogens and progestins.  The mechanism is decreased clearance of the hormones due to inhibition of CYP450 3A4 activity by azole antifungals.', 'DDInter', 1767317477, 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', 'Metabolism', 'During concomitant therapy with azole antifungal agents, patients should be observed for increased or altered pharmacologic response to estrogens and progestins, and dosage(s) adjusted accordingly as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575423, 'Fosamprenavir', 'Dienogest', 'Major', 'Some hormonal contraceptives may decrease the plasma concentrations of amprenavir from fosamprenavir when it is administered in the absence of low-dose ritonavir as a pharmacokinetic booster.  In contrast, fosamprenavir in combination with ritonavir may reduce the plasma levels of certain contraceptive hormones, and there is also an increased risk of transaminase elevations when these agents are used together.  The mechanism of interaction has not been described.', 'DDInter', 1767317477, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.e., nonhormonal) methods of birth control should be considered in patients treated with fosamprenavir.', 'Others', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, alternative (i.', NULL, NULL, 'Major', 'pharmacodynamic');
