INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575466, 'Dienogest', 'Miltefosine', 'Moderate', 'The vomiting and/or diarrhea that commonly occur during miltefosine therapy may affect the absorption of oral contraceptives, potentially compromising their efficacy.', 'DDInter', 1767317477, 'Female patients receiving oral contraceptives should be advised to use additional non-hormonal or alternative method(s) of effective contraception if vomiting or diarrhea occurs during miltefosine therapy.  This is particularly important because miltefosine may cause fetal harm.  Teratogenicity and fetal death were observed in animals administered miltefosine at dosages lower than the recommended human dosage.  Females of reproductive potential should use effective contraception during miltefosine therapy and for 5 months after completion of therapy.', 'Absorption', 'Female patients receiving oral contraceptives should be advised to use additional non-hormonal or alternative method(s) of effective contraception if vomiting or diarrhea occurs during miltefosine therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
