INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575468, 'Dienogest', 'Modafinil', 'Moderate', 'Coadministration with modafinil (the racemate) or armodafinil (the R-enantiomer) may decrease the plasma concentrations of ethinyl estradiol and other contraceptive hormones.  The mechanism involves induction of CYP450 3A4-mediated metabolism by modafinil and armodafinil.', 'DDInter', 1767317477, 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil.  Alternative or additional methods of birth control are recommended during and for one month after treatment with modafinil or armodafinil.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives (including depot and implantable contraceptives) should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with modafinil or armodafinil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575469, 'Dienogest', 'Morphine', 'Minor', 'Oral contraceptives induce glucuronidation and may decrease plasma concentrations of morphine, diamorphine, and other opioids.', 'DDInter', 1767317477, 'Oral contraceptives may influence opioid analgesic effects and opioid dosage may need to be adjusted.', 'Metabolism', 'Oral contraceptives may influence opioid analgesic effects and opioid dosage may need to be adjusted.', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575470, 'Dienogest', 'Mycophenolate mofetil', 'Major', 'Coadministration with mycophenolic acid may decrease the plasma concentrations and, theoretically, the efficacy of contraceptive hormones.  The mechanism of interaction has not been established.', 'DDInter', 1767317477, 'Although clinical significance of the interaction is unknown, caution is advised when mycophenolic acid is prescribed in combination with hormonal contraceptives, including all oral, injectable, transdermal, vaginal, and implantable forms.  Because use of mycophenolic acid is associated with an increased risk of pregnancy loss in the first trimester and congenital malformations, it is particularly important that female patients not become pregnant during treatment.  An acceptable barrier method (e.g., diaphragm with spermicide, cervical cap with spermicide, contraceptive sponge, male condom, female condom) should be used in addition to the hormonal contraceptive of choice during the entire duration of mycophenolic acid therapy and for 6 weeks after discontinuation.  Input from a gynecologist or similar expert on adequate contraception should be sought as needed.  Intrauterine systems are unlikely to be significantly affected because of their local action.', 'Others', 'Although clinical significance of the interaction is unknown, caution is advised when mycophenolic acid is prescribed in combination with hormonal contraceptives, including all oral, injectable, transdermal, vaginal, and implantable forms.', NULL, NULL, 'Major', 'pharmacodynamic');
