INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575475, 'Dienogest', 'Nelfinavir', 'Moderate', 'Nelfinavir may decrease the plasma concentrations of contraceptive hormones during chronic coadministration.  The proposed mechanism is nelfinavir induction of the hepatic metabolism of sex hormones.', 'DDInter', 1767317477, 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', 'Metabolism', 'When using Nelfinavir and birth control pills together, we recommend additional contraception.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575476, 'Dienogest', 'Neratinib', 'Moderate', 'Coadministration with neratinib may reduce the efficacy of hormonal contraceptives.  The mechanism for the potential interaction has not been established.', 'DDInter', 1767317477, 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.  Women should avoid becoming pregnant during treatment with neratinib and for up to 1 month after discontinuing treatment.  Women using hormonal contraceptives should add a barrier method.  In addition, men receiving neratinib should use a barrier method of contraception during treatment and for 3 months after discontinuing treatment.', 'Others', 'Although the clinical significance of this interaction is unknown, caution is advised when neratinib is prescribed concomitantly with hormonal contraceptives because of the potential to cause fetal harm.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575477, 'Dienogest', 'Nevirapine', 'Moderate', 'Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is nevirapine induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.', 'DDInter', 1767317477, 'ADDITIONAL CONTRACEPTION RECOMMENDED:  Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is nevirapine induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.  In ten HIV-positive subjects, nevirapine (200 mg once a day for 2 weeks followed by 200 mg twice a day for 2 weeks) decreased the area under the plasma concentration-time curve (AUC) of ethinyl estradiol (administered as ethinyl estradiol-norethindrone 0.035 mg-1 mg) by 20% compared to administration of the oral contraceptive alone.  The AUC and peak plasma concentration (Cmax) of norethindrone decreased by 19% and 16%, respectively.', 'Metabolism', 'ADDITIONAL CONTRACEPTION RECOMMENDED:  Coadministration with nevirapine may decrease the plasma concentrations of contraceptive hormones.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575478, 'Dienogest', 'Olaparib', 'Moderate', 'Coadministration with olaparib may decrease the plasma concentrations and efficacy of contraceptive hormones.  The proposed mechanism is olaparib induction of CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of sex hormones.', 'DDInter', 1767317477, 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.  In women of childbearing potential, alternative or additional non-hormonal methods of birth control should be considered during treatment and for one month after receiving the last dose of olaparib.  Regular pregnancy tests are also recommended.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Some authorities recommend that women avoid pregnancy during olaparib treatment due to its potential teratogenic effects.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575479, 'Dienogest', 'Oxcarbazepine', 'Major', 'Coadministration with certain anticonvulsants such as carbamazepine, eslicarbazepine, oxcarbazepine, phenobarbital, phenytoin, and primidone may reduce the efficacy of contraceptive hormones.', 'DDInter', 1767317477, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.  Alternative or additional methods of birth control should be used during and for at least two weeks after short-term and 4 weeks after long-term (greater than 4 weeks) anticonvulsant therapy.  If a combination oral contraceptive pill is used, a regimen containing at least 50 mcg of ethinyl estradiol per day or equivalent should be considered.  Although breakthrough bleeding is not necessarily indicative of low ethinyl estradiol serum levels or increased risk of ovulation, some clinicians suggest that women who experience breakthrough bleeding during enzyme-inducing therapy may be prescribed an increased dose of ethinyl estradiol above 50 mcg daily by combining more than one formulation of contraceptive pill if necessary.  For emergency contraception in patients who have used an hepatic enzyme inducer in the past 4 weeks, a non-hormonal emergency contraceptive (e.g., copper intrauterine device) is considered preferable.  If this is not possible, some authorities recommend that the usual dose of levonorgestrel (1.5 mg) should be doubled to 3 mg and taken as a single dose as soon as possible (within 72 hours of unprotected sexual intercourse).  However, there are no data on efficacy, compliance, or side effects of this regimen.  For women with the etonogestrel subdermal implant, the addition of a barrier method is recommended during concomitant use and for 28 days after discontinuation of hepatic enzyme inducing drugs.  It is recommended to remove the implant and to prescribe a nonhormonal method in women who require long-term treatment with hepatic enzyme inducing drugs.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by enzyme-inducing drugs.', 'Antagonism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575480, 'Dienogest', 'Oxtriphylline', 'Moderate', 'The coadministration with contraceptive steroids may increase the plasma concentrations of theophylline and other methylxanthines.  The proposed mechanism is inhibition of methylxanthine metabolism via hepatic microsomal enzymes, of which estrogens and progestins are also substrates.', 'DDInter', 1767317477, 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.  Serum theophylline levels and pharmacologic response should be monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of hormonal contraceptives in patients who are stabilized on their theophylline regimen.  Patients should be advised to notify their physician if they experience signs and symptoms of theophylline toxicity such as nausea, vomiting, diarrhea, headache, restlessness, insomnia, and irregular heartbeat.', 'Metabolism', 'Caution is advised during concomitant therapy with methylxanthines and hormonal contraceptives.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575481, 'Dienogest', 'Pegaspargase', 'Moderate', 'Coadministration with another thiazolidinedione, troglitazone, has been shown to decrease the plasma concentrations of contraceptive hormones.  The proposed mechanism is induction of hormone metabolism via CYP450 3A4.', 'DDInter', 1767317477, 'The use of oral contraceptives during asparaginase treatment is not recommended.  Other non-oral contraceptive methods should be used in women of childbearing potential.  According to the manufacturer, females of reproductive potential should be advised to use effective contraception during asparaginase treatment and for at least 3 to 6 months after the last dose.', 'Metabolism', 'The use of oral contraceptives during asparaginase treatment is not recommended.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575482, 'Dienogest', 'Pentobarbital', 'Moderate', 'Coadministration with a barbiturate may reduce the efficacy of contraceptive hormones.The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity and enhancement of hormone-binding globulin capacity by phenobarbital.  Since all barbiturates are believed to possess enzyme-inducing capabilities, the interaction should be expected with agents in the class other than phenobarbital.', 'DDInter', 1767317477, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.  Alternative or additional methods of birth control should be used during and for at least one week after short-term and 4 weeks after long-term (greater than 4 weeks) barbiturate therapy.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Injectable progestin-only contraceptives are also thought to be unaffected by barbiturates.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with barbiturates.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575483, 'Dienogest', 'Perampanel', 'Moderate', 'Coadministration with certain anticonvulsants, such as rufinamide and perampanel, may reduce the efficacy of contraceptive hormones.  The interaction stems from accelerated clearance of contraceptive hormones as well as decreased plasma concentrations of unbound (active) hormones due to induction of hepatic CYP450 enzymatic activity by the anticonvulsants.', 'DDInter', 1767317477, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.  In general, alternative or additional methods of birth control should be used during and for at least two weeks after short-term and 4 weeks after long-term (greater than 4 weeks) anticonvulsant therapy.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with enzyme-inducing anticonvulsants.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575484, 'Dienogest', 'Pexidartinib', 'Major', 'Coadministration with pexidartinib may decrease the plasma concentration and efficacy of contraceptive hormones.  The proposed mechanism is increased clearance due to pexidartinib-mediated induction of CYP450 3A4, the isoenzyme partially responsible for the metabolic clearance of sex hormones and other steroids.', 'DDInter', 1767317477, 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with pexidartinib.  Alternative or additional methods of birth control should be used during and for at least one month after the final dose of pexidartinib.  In addition, male patients with female partners of reproductive potential should use effective contraception during and for at least one week after the final dose of pexidartinib.  No precautions or recommendations are available for women using hormone-releasing intrauterine systems, but a significant interaction with these systems is thought to be unlikely due to their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with pexidartinib.', NULL, NULL, 'Major', 'pharmacodynamic');
