INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575486, 'Dienogest', 'Phenylbutazone', 'Moderate', 'Limited clinical data suggest that phenylbutazone may reduce the efficacy of contraceptive hormones, potentially resulting in menstrual abnormalities (e.g., breakthrough bleeding, amenorrhea, irregular menses) or unintended pregnancy.  The proposed mechanism is accelerated clearance of the hormones due to induction of hepatic CYP450 enzymes by phenylbutazone.', 'DDInter', 1767317477, 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with phenylbutazone.  Alternative or additional methods of birth control should be used during and probably for at least one week after short-term and up to one month after long-term (greater than 4 weeks) phenylbutazone therapy.  Intrauterine systems are unlikely to be significantly affected because of their local action.  Input from a gynecologist or similar expert on adequate contraception, including emergency contraception, should be sought as needed.', 'Metabolism', 'Women using low-dose hormonal contraceptives should be advised of the risk of breakthrough bleeding and unintended pregnancy during concomitant therapy with phenylbutazone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
