INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575534, 'Dienogest', 'Zolmitriptan', 'Minor', 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.The mechanism and clinical significance are unknown.', 'DDInter', 1767317477, 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.  Mean Cmax and AUC of zolmitriptan were 30% and 50% higher, respectively, and Tmax was delayed by one-half hour in females taking oral contraceptives.  The mechanism and clinical significance are unknown.  The effect of zolmitriptan on the pharmacokinetics of oral contraceptives has not been reported.  Patients should be advised to notify their physician promptly if they experience sedation, dizziness, paresthesia, chest pain or tightness, shortness of breath, or irregular heartbeats.', 'Others', 'Retrospective pharmacokinetic analysis indicate that plasma concentrations of zolmitriptan may be higher in females taking concurrent oral contraceptives.', NULL, NULL, 'Minor', 'pharmacodynamic');
