INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575658, 'Donepezil', 'Somapacitan', 'Moderate', 'Coadministration with drugs that are inducers of the CYP450 3A4 and/or 2D6 enzymatic pathways may decrease the plasma concentrations of donepezil.  The mechanism is accelerated clearance of donepezil due to enhanced CYP450 activities.', 'DDInter', 1767317477, 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', 'Metabolism', 'While clinical data are lacking, the possibility of a diminished therapeutic response to donepezil should be considered in patients receiving concomitant inducer agents.', NULL, NULL, 'Moderate', 'pharmacodynamic');
