INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575719, 'Grazoprevir', 'Atorvastatin', 'Major', 'Coadministration with elbasvir-grazoprevir may significantly increase the plasma concentrations of HMG-CoA reductase inhibitors (statins) that are substrates of the CYP450 3A4 isoenzyme and/or breast cancer resistance protein (BCRP) transporter.  Grazoprevir is reported to be a relatively weak inhibitor of CYP450 3A4 in vivo, and both elbasvir and grazoprevir are inhibitors of BCRP at the intestinal level in humans.', 'DDInter', 1767317477, 'Caution is advised when statins such as atorvastatin, rosuvastatin, fluvastatin, lovastatin, and simvastatin are prescribed with elbasvir-grazoprevir.  Atorvastatin dosage should not exceed 20 mg/day and rosuvastatin dosage should not exceed 10 mg/day according to the product labeling for elbasvir-grazoprevir.  The lowest effective dose of fluvastatin, lovastatin, or simvastatin should be used; some authorities recommend a maximum daily dose of 20 mg of these statins.  In contrast, pitavastatin and pravastatin do not have clinically relevant interactions with elbasvir-grazoprevir, and no dosage adjustments are necessary.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution is advised when statins such as atorvastatin, rosuvastatin, fluvastatin, lovastatin, and simvastatin are prescribed with elbasvir-grazoprevir.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575720, 'Grazoprevir', 'Bempedoic acid', 'Major', 'Coadministration with inhibitors of organic anion transporting polypeptides (OATP) 1B1 and/or 1B3 may significantly increase the plasma concentrations of grazoprevir, which is a substrate of the hepatic uptake transporters.', 'DDInter', 1767317477, 'Concomitant use of elbasvir-grazoprevir with OATP 1B1 or 1B3 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of elbasvir-grazoprevir with OATP 1B1 or 1B3 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575721, 'Elbasvir', 'Berotralstat', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may significantly increase the plasma concentrations of berotralstat.  Berotralstat is a substrate of both P-gp and BCRP.', 'DDInter', 1767317477, 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.  Patients should be advised to contact their physician if they experience undue adverse effects of berotralstat such as abdominal pain, vomiting, or diarrhea.  Patients should seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitations, irregular heartbeat, shortness of breath, or syncope.', 'Metabolism', 'The dosage of berotralstat should be reduced to 110 mg once daily in patients requiring chronic coadministration with P-gp and/or BCRP inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
