INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575723, 'Elbasvir', 'Betrixaban', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of betrixaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'Caution is advised when betrixaban is used with P-gp inhibitors.  Closer monitoring of the pharmacologic effects of betrixaban may be appropriate whenever a P-gp inhibitor is added to or withdrawn from therapy.  Patients should be routinely evaluated for signs and symptoms suggesting blood loss such as a drop in hemoglobin and/or hematocrit, hypotension, or fetal distress (in pregnant women).  Concomitant use of betrixaban with P-gp inhibitors should be avoided in patients with severe renal impairment (CrCl<30 mL/min).', 'Excretion', 'Caution is advised when betrixaban is used with P-gp inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
