INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575945, 'Grazoprevir', 'Sunitinib', 'Moderate', 'Coadministration of sunitinib with elbasvir-grazoprevir may increase the plasma concentrations and the risk of adverse effects of sunitinib.  The proposed mechanism by which elbasvir-grazoprevir increases sunitinib exposure has not been clearly delineated, but may involve inhibition of intestinal breast cancer resistance protein (BCRP).', 'DDInter', 1767317477, 'Although the interaction has not been studied, caution is advised if sunitinib and elbasvir-grazoprevir are used in combination.  Dose adjustment of sunitinib as well as clinical and laboratory monitoring of sunitinib should be considered whenever elbasvir-grazoprevir is added to or withdrawn from therapy.  During concomitant treatment, patients should be monitored for signs and symptoms of sunitinib toxicity such as diarrhea, hypertension, and/or neutropenia.', 'Absorption', 'Although the interaction has not been studied, caution is advised if sunitinib and elbasvir-grazoprevir are used in combination.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575946, 'Grazoprevir', 'Suvorexant', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of grazoprevir, which is a substrate of the isoenzyme.', 'DDInter', 1767317477, 'Caution is advised if elbasvir-grazoprevir is prescribed in combination with CYP450 3A4 inhibitors.  Patients should be monitored for increased adverse effects such as nausea, vomiting, and ALT elevations.', 'Metabolism', 'Caution is advised if elbasvir-grazoprevir is prescribed in combination with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575947, 'Grazoprevir', 'Tacrolimus', 'Moderate', 'Coadministration with elbasvir-grazoprevir may increase the blood concentrations of tacrolimus.  The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of tacrolimus by grazoprevir, a weak inhibitor of the isoenzyme.', 'DDInter', 1767317477, 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir.  Renal function and tacrolimus blood levels should be monitored more closely whenever elbasvir-grazoprevir is added to or discontinued from therapy, and the tacrolimus dosage adjusted as necessary.  Patients should be advised to contact their doctor if they experience potential signs and symptoms of tacrolimus toxicity such as fever, infection, diarrhea, tremor, headache, fatigue, lethargy, and changes in motor function, mental status, or sensory function.', 'Metabolism', 'Given the narrow therapeutic index and the large interindividual variability in the pharmacokinetics of tacrolimus, caution is advised if the drug is used in combination with elbasvir-grazoprevir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575948, 'Elbasvir', 'Talazoparib', 'Moderate', 'Coadministration with sparsentan may decrease the plasma concentrations of drugs that are substrates of the CYP450 2B6, 2C9, and 2C19 isoenzymes.  The proposed mechanism is increased clearance due to sparsentan-mediated induction of CYP450 2B6, 2C9, and 2C19.', 'DDInter', 1767317477, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.  However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Metabolism', 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, NULL, 'Moderate', 'pharmacodynamic');
