INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575975, 'Elexacaftor', 'Ambrisentan', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3.  The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite.', 'DDInter', 1767317477, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
