INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575994, 'Elexacaftor', 'Norgestrel', 'Moderate', 'Concomitant use of hormonal contraceptives and ivacaftor/tezacaftor/elexacaftor (IVA/TEZ/ELX) may increase the risk for rash events.', 'DDInter', 1767317477, 'Monitor for rash in patients taking both medications.  Consider interrupting IVA/TEZ/ELX and hormonal contraceptives in patients who develop rash.  Once the rash clears, IVA/TEZ/ELX may be restarted without the hormonal contraceptives.  If rash does not recur, resumption of hormonal contraceptives can be considered.', 'Others', 'Monitor for rash in patients taking both medications.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1575995, 'Elexacaftor', 'Olmesartan', 'Moderate', 'Coadministration with elexacaftor may increase the plasma concentrations of drugs that are substrates of the organic anion transporting polypeptides (OATP) 1B1 and 1B3.  The proposed mechanism, based on in vitro data, involves decreased metabolic clearance due to inhibition of OATP 1B1- and 1B3-mediated hepatic uptake by elexacaftor and its metabolite.', 'DDInter', 1767317477, 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.  Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever elexacaftor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when elexacaftor is used concurrently with drugs that are known OATP 1B1 and 1B3 substrates, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
