INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576065, 'Sacubitril', 'Lovastatin', 'Moderate', 'Coadministration with sacubitril may increase the plasma concentrations of drugs that are substrates of organic anion transporting polypeptides (OATP) 1B1 and 1B3, such as some HMG-CoA reductase inhibitors (i.e., statins).  The proposed mechanism is sacubitril-mediated inhibition of hepatic uptake transporters OATP1B1 and/or OATP1B3.', 'DDInter', 1767317477, 'Caution and close monitoring are advised if sacubitril must be used concomitantly with substrates of OATP1B1 and OATP1B3.  All patients receiving statin therapy should be advised to promptly report any unexplained muscle pain, tenderness or weakness, particularly if accompanied by fever, malaise and/or dark colored urine.  Therapy should be discontinued if creatine kinase is markedly elevated in the absence of strenuous exercise or if myopathy is otherwise suspected or diagnosed.', 'Metabolism', 'Caution and close monitoring are advised if sacubitril must be used concomitantly with substrates of OATP1B1 and OATP1B3.', NULL, NULL, 'Moderate', 'pharmacodynamic');
