INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576113, 'Doxorubicin', 'Velpatasvir', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of daunorubicin and idarubicin, both of which are substrates of the efflux transporter also known as ABCB1 or MDR1.', 'DDInter', 1767317477, 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.  Patients should be closely monitored for increased adverse effects including cardiotoxicity and myelosuppression.', 'Absorption', 'Caution is advised if daunorubicin or idarubicin is prescribed in combination with a P-gp inhibitor.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576114, 'Dexlansoprazole', 'Velpatasvir', 'Major', 'Coadministration of velpatasvir with proton pump inhibitors may reduce its gastrointestinal absorption.  Velpatasvir exhibits pH-dependent solubility, with increased solubility at lower pH.', 'DDInter', 1767317477, 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.  If coadministration is required, sofosbuvir-velpatasvir should be administered with food and taken 4 hours before the proton-pump inhibitor at a maximum daily dose equivalent to omeprazole 20 mg.  Use with other proton-pump inhibitors has not been studied.', 'Absorption', 'Concomitant use of sofosbuvir-velpatasvir with proton-pump inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576115, 'Dicoumarol', 'Velpatasvir', 'Moderate', 'Liver function changes due to treatment with direct-acting HCV antiviral agents may affect the therapeutic efficacy of vitamin K antagonists.  The mechanism may involve potential pharmacokinetic changes for some vitamin K antagonists due to altered metabolism, or patients may exhibit alterations in hepatic synthesis of vitamin K-dependent coagulation factors and clearance of activated factors that could impact response to vitamin K antagonists.', 'DDInter', 1767317477, 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', 'Others', 'The INR should be monitored more closely following initiation or discontinuation of a direct-acting HCV antiviral agent, and the dosage of vitamin K antagonist adjusted if necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576116, 'Digoxin', 'Velpatasvir', 'Moderate', 'Coadministration with velpatasvir may increase the serum concentrations of digoxin.  The proposed mechanism is velpatasvir inhibition of the P-glycoprotein-mediated intestinal or renal efflux of digoxin.', 'DDInter', 1767317477, 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of sofosbuvir-velpatasvir, and the digoxin dosage adjusted as needed.  Patients should be advised to seek medical attention if they experience signs of digoxin toxicity such as nausea, anorexia, visual disturbances, slow pulse, or irregular heartbeat.', 'Absorption, Excretion', 'Serum digoxin levels and pharmacologic effects should be closely monitored following the addition or withdrawal of sofosbuvir-velpatasvir, and the digoxin dosage adjusted as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576117, 'Dulaglutide', 'Velpatasvir', 'Moderate', 'Clearance of hepatitis C virus (HCV) infection with direct acting antiviral agents (DAAs) may lead to changes in hepatic function which may result in altered blood glucose control.  Serious symptomatic hypoglycemia has been reported in diabetic patients in postmarketing case reports and published epidemiological studies.', 'DDInter', 1767317477, 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.  The patient as well as the healthcare providers in charge of diabetic care should be apprised of the risk of hypoglycemia.  Patients should be aware of the potential signs and symptoms of hypoglycemia such as headache, dizziness, drowsiness, nervousness, confusion, tremor, hunger, weakness, perspiration, palpitation, and tachycardia.  For antidiabetic medications that are not glucose-dependent, reduction in the dosage should be considered to mitigate the risk of hypoglycemia.', 'Synergism', 'Blood glucose should be closely monitored during treatment of HCV with DAAs, particularly during the first 3 months, and appropriate changes made to the antidiabetic drug regimen as needed.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576118, 'Echinacea', 'Velpatasvir', 'Moderate', 'Coadministration with echinacea may alter the plasma concentrations and therapeutic effects of drugs that are substrates of CYP450 3A4.  Echinacea appears to inhibit intestinal CYP450 3A4, which would lead to an increase in oral midazolam (a sensitive 3A4 substrate) bioavailability; however, plasma levels of midazolam following oral administration do not appear to be affected by echinacea.  In contrast, it appears that echinacea may also induce hepatic CYP450 3A4; thereby increasing the hepatic clearance of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317477, 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.  If echinacea is prescribed with a drug that is a CYP450 3A4 substrate, the possibility of an altered (increased or decreased) therapeutic response should be considered.  Patients should be monitored more closely following the addition or withdrawal of echinacea and the dosage of the CYP450 3A4 substrate adjusted as necessary.', 'Metabolism', 'In general, patients should be advised to consult their healthcare provider before using any herbal or alternative medicines.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1576119, 'Edoxaban', 'Velpatasvir', 'Major', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the plasma concentrations of edoxaban, which is a substrate of the efflux transporter.', 'DDInter', 1767317477, 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.  This dosage recommendation is based on data from a clinical study, the Hokusai VTE study, and is limited to use with the specific P-gp inhibitors mentioned.  Other P-gp inhibitors were not permitted in the study, and patients on antiretroviral therapy (ritonavir, nelfinavir, indinavir, saquinavir) as well as cyclosporine were excluded from the study.  Following discontinuation of the P-gp inhibitor, edoxaban dosage should be returned to the regular dosage of 60 mg once daily.  No dosage adjustment is recommended for edoxaban when used in the treatment of nonvalvular atrial fibrillation.', 'Absorption', 'When used for the treatment of deep vein thrombosis and pulmonary embolism, the manufacturer recommends that edoxaban dosage be reduced to 30 mg once daily in patients receiving concomitant treatment with certain P-gp inhibitors including azithromycin, clarithromycin, erythromycin, oral itraconazole, oral ketoconazole, quinidine, and verapamil.', NULL, NULL, 'Major', 'pharmacodynamic');
